mapper.update:
update tbl_reply set
reply_text=#{reply_text}
,update_date = systimestamp
where rno=#{rno}

update tbl_board set
reply_count= (select count(*) from tbl_reply where tbl_reply.bno=#{bno})
where tbl_board.bno=#{bno}

mapper.select:
select * from
(
select rownum, tableA.* from
(
	select * from tbl_reply
	where bno = #{bno}
	order by rno desc
)tableA

where rownum < #{pageVO.queryStartNum}+#{pageVO.queryPerPageNum}
)tableB
where rownum >= #{pageVO.queryStartNum}
